        var uye_id = <?php echo json_encode($_SESSION['id']); ?>;
        function updateOnlineCount() {
            $.ajax({
                type: 'POST',
                url: 'functions/online_count.php',
                data: {
                    uye_id: uye_id,
                    type: 'updateOnlineStatus'
                }
            });
        }

        setInterval(updateOnlineCount, 10000);
